@Composable
fun HomeBottomBar() {
    var selectedItem by remember { mutableStateOf(1) } // Default to Activity (index 1)

    NavigationBar(
        containerColor = Color.White,
        contentColor = Gray400,
        tonalElevation = 0.dp,
        modifier = Modifier.border(1.dp, Gray100)
    ) {
        val items = listOf(
            BottomNavItem("Home", BottomNavIcons.HomePath, 19f, 21f),
            BottomNavItem("Activity", BottomNavIcons.ActivityPath, 17f, 24f),
            BottomNavItem("Shop", BottomNavIcons.ShopPath, 24f, 24f),
            BottomNavItem("Profile", BottomNavIcons.ProfilePath, 24f, 24f)
        )

        items.forEachIndexed { index, item ->
            val isSelected = selectedItem == index
            NavigationBarItem(
                selected = isSelected,
                onClick = { selectedItem = index },
                icon = {
                    SvgIcon(
                        pathData = item.path,
                        viewportWidth = item.viewportWidth,
                        viewportHeight = item.viewportHeight,
                        tint = if (isSelected) ActivityGreen else Gray400
                    )
                },
                label = {
                    Text(
                        text = item.label,
                        style = MaterialTheme.typography.labelSmall.copy(
                            fontWeight = FontWeight.SemiBold,
                            fontSize = 12.sp
                        )
                    )
                },
                alwaysShowLabel = false, // Hides label when unselected
                colors = NavigationBarItemDefaults.colors(
                    selectedIconColor = ActivityGreen,
                    selectedTextColor = ActivityGreen,
                    unselectedIconColor = Gray400,
                    unselectedTextColor = Gray400,
                    indicatorColor = Color.Transparent
                )
            )
        }
    }
}

