private data class BottomNavItem(
    val label: String,
    val path: String,
    val viewportWidth: Float,
    val viewportHeight: Float
)

// Helper Composable to draw SVG paths
@Composable
fun SvgIcon(
    pathData: String,
    viewportWidth: Float,
    viewportHeight: Float,
    tint: Color,
    modifier: Modifier = Modifier
) {
    Canvas(modifier = modifier.size(24.dp)) {
        val path = PathParser.createPathFromPathData(pathData).asComposePath()

        // Calculate scale to fit within the 24dp box while preserving aspect ratio
        val scale = min(size.width / viewportWidth, size.height / viewportHeight)

        // Center the path
        val scaledWidth = viewportWidth * scale
        val scaledHeight = viewportHeight * scale
        val translateX = (size.width - scaledWidth) / 2
        val translateY = (size.height - scaledHeight) / 2

        with(drawContext.canvas) {
            translate(translateX, translateY)
            scale(scale, scale)
            drawPath(path, tint)
        }
    }
}

